import React from "react";
import StoreShopPage from "../StoreShopPage";
import StoreCartPage from "../StoreCartPage";
import WeatherCard from "../../weather";
import items from "../constants";

const StoreContent = ({ cart, onAddToCart, onRemoveItem,tab }) => {
  switch (tab) {
    default:
    case "shop":
      return <StoreShopPage items={items} onAddToCart={onAddToCart} />;
    case "cart":
      return (
        <StoreCartPage
          items={cart}
          onPlus={onAddToCart}
          onMinus={onRemoveItem}
        />
      );
      case "weather":
        return (
          <WeatherCard/>
        )
  }
};

export default StoreContent;
